mapper.select:
select r.recruit_id,r.company_id,a.company_name,recruit_name,r.recruit_address,recruit_time,recruit_tag,recruit_salary_max,recruit_salary_min,recruit_number,recruit_degree,recruit_age,recruit_exp,recruit_type,recruit_text_duty,recruit_text_need,job_field from tb_apply a join tb_recruit r on a.company_id = r.company_id and a.recruit_id = r.recruit_id
and recruit_name like concat('%' #{like} '%')
and user_id = #{userId}
